mapper.insert:

	SELECT seq_bo_seq_no.nextval from dual

INSERT INTO tb_board (
    bo_seq_no
  , bo_type
  , bo_title
  , bo_content
  , bo_writer
  , bo_hit_cnt
  , bo_open_yn
  , bo_del_yn
  , reg_user
  , reg_date
  , upd_user
  , upd_date
) VALUES (
    #{bo_seq_no}  
  , #{bo_type}
  , #{bo_title}
  , #{bo_content}
  , #{bo_writer}
  , 0
  , NVL(#{bo_open_yn},'N')
  , 'N'
  , #{bo_writer}
  , SYSDATE  
  , #{bo_writer}
  , SYSDATE   
)

mapper.update:
UPDATE tb_board SET
	bo_type        = #{bo_type}
,	bo_title       = #{bo_title}
,	bo_content     = #{bo_content}
,	bo_open_yn     = #{bo_open_yn}		
,	upd_user       = #{upd_user}
,	upd_date       = SYSDATE
WHERE bo_seq_no = #{bo_seq_no}

mapper.delete:
UPDATE tb_board SET
	bo_del_yn     = 'Y'		
,	upd_user       = #{upd_user}
,	upd_date       = SYSDATE
WHERE bo_seq_no = #{bo_seq_no}

mapper.update:
UPDATE tb_board SET
	bo_hit_cnt = bo_hit_cnt + 1
WHERE bo_seq_no = #{bo_seq_no}
