mapper.update:
  UPDATE tb_member SET
	  mem_name              = #{mem_name}	/* 회원이름 */
	, mem_pwd               = #{mem_pwd}
	, mem_birth             = #{mem_birth}
	, mem_phone             = #{mem_phone}
	, mem_email             = #{mem_email}
	, mem_zipcode           = #{mem_zipcode}
	, mem_addr_master       = #{mem_addr_master}
	, mem_addr_detail       = #{mem_addr_detail}
	, upd_user              = #{mem_id}
	, upd_date              = SYSDATE
  WHERE mem_id = #{mem_id}
